mapper.select:
select id, course_id, question_text, question_type, correct_answer, explanation, difficulty_level, create_time, update_time
from quiz_question
where id = #{id}

select id, course_id, question_text, question_type, correct_answer, explanation, difficulty_level, create_time, update_time
from quiz_question


        and course_id = #{courseId}


        and difficulty_level = #{difficultyLevel}


order by create_time desc

select id, course_id, question_text, question_type, correct_answer, explanation, difficulty_level, create_time, update_time
from quiz_question
where course_id = #{courseId}

    and difficulty_level = #{difficultyLevel}

order by rand()
limit #{limit}
